import React, { useState, useRef } from 'react'
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import styles from '../../styles/PostPage.module.css';
import { useCategoriesContext } from "../../contexts/usePostProvider/usePostList";
import { FaNode } from "react-icons/fa";
import { IoLogoJavascript } from "react-icons/io5";
import { SiTypescript } from "react-icons/si";
import { SiFirebase } from "react-icons/si";
import { BsBootstrapFill } from "react-icons/bs";
import { FaReact } from "react-icons/fa";
import { FaGithub } from "react-icons/fa";


interface FormData {
  createdPost: {
    id: string;
    title: string;
    content: string;
    category_id: string;
    imageUrl: string;
  };
  title: string;
  content: string;
}

const PostPage: React.FC = () => {

  const { titleRef, contentRef, selectRef, handleSubmitPost, handleFileChange } = useCategoriesContext();

  const [addedIconsList, setAddedIconsList] = useState<JSX.Element[]>([]);
  const [selectedIconItems, setSelectedIconItems] = useState<number>(0);


  const techIcons = [
   ' <FaNode />',
    '<IoLogoJavascript />',
   ' <SiTypescript />',
    '<SiFirebase />',
    '<BsBootstrapFill />',
    '<FaReact />',
   ' <FaGithub />'
  ];

  const addIcon = (iconItem, index: number) => {
    const iconsList = [...addedIconsList];
    iconsList.push(iconItem);
    setAddedIconsList(iconsList);
  };
  return (
    <div className={styles.postpage_container}>
      <h1 style={{ color: 'black' }}>PostPage</h1>
      <Form onSubmit={handleSubmitPost} style={{ padding: '5rem', height: '100%' }}>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Title</Form.Label>
          <Form.Control
            type="text"
            ref={titleRef}
            placeholder="Add post title"
            className={styles.form_title_area}
          />
          <Form.Text className="text-muted">
            Add a title
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Add text</Form.Label>
          <Form.Control
            as="textarea"
            ref={contentRef}
            placeholder="Add post content"
            className={styles.form_text_area}
          />
        </Form.Group>
        <Form.Group className={styles.file_upload}>
          <Form.Label>Add image</Form.Label>
          <Form.Control
            type='file'
            onChange={handleFileChange}
            accept="image/*"
          />

        </Form.Group>
        <Form.Control aria-label="Default select example"
          as="select" ref={selectRef}
          className={styles.select_category_wrapper}>
          <option value="">Select post category</option>
          <option value="about">about</option>
          <option value="hero">hero</option>
          <option value="workexperience">workexperience</option>
          <option value="project">project</option>
        </Form.Control>

        <div className={styles.tech_icons} style={{display:'flex', fontSize:'1.5rem'}}>
          {techIcons.map((item, index: number) => {
            return (
              <div
              className={`${styles.icon} ${selectedIconItems === index ? styles.selectedIconItem : ''}`}
              key={index}
              onClick={() => {
                setSelectedIconItems(index);
                addIcon(item);
              }}
              style={{ marginRight: '0.5rem', cursor: 'pointer' }}
            >
              {item}
            </div>
            )
          })}

        </div>


        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    </div>
  )
}



export default PostPage
