import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaTrashAlt, FaEdit } from 'react-icons/fa';
import Image from 'react-bootstrap/Image';
import styles from '../styles/Post.module.css';

interface PostProps {
  imageUrl: string;
  title: string;
  id: number;
  handleDeletePost: (id: number) => void;
  handlePostClick: (id: number) => void;
}

const Post: React.FC<PostProps> = ({
  imageUrl,
  title,
  id,
  handleDeletePost,
  handlePostClick,
}) => {

  const navigate = useNavigate();


  const goToPost = (id: number) => {
      navigate(`http://localhost:3001/api//posts/${id}`);
    };


  return (

    <tr key={id} className={styles.tableRow} onClick={() => goToPost(id)}>

      <td>
        <Image src={imageUrl} alt="Thumbnail" className={styles.dashboardImg} />
      </td>
      <td className={styles.titleColumn}>
        <Link to={`http://localhost:3001/api/posts/${id}`}>
          {title}    </Link>
      </td>
      <td className={styles.iconColumn}>
        <FaEdit />
      </td>
      <td className={styles.iconColumn}>
        <FaTrashAlt onClick={() => handleDeletePost(id)} />
      </td>

    </tr>

  );
};

export default Post;
