import { useState, useEffect, createContext, useContext } from 'react';
import axios from 'axios';


interface Category {
    categories:string;
    category_id:string;
    content:string;
    title:string;
    post_img:string;
  }

  const CategoriesContext = createContext<Category[]>([])

  export const useCategoriesContext = () => {
    return useContext(CategoriesContext)

  }

  export const CategoriesProvider: React.FC = ({ children }: React.PropsWithChildren<{}>) => {

    const [posts, setPosts] = useState<Category[]>([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/posts/');
        setPosts(response.data);
        console.log(response.data,'responseee data hookcontext')
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <CategoriesContext.Provider value={posts}>
        {children}
    </CategoriesContext.Provider>
  )
};
