import React, { useState, useEffect } from 'react';
import { useCategoriesContext } from "../contexts/usePostProvider/usePostList";

import axios from 'axios';
import { useParams } from 'react-router-dom';


interface Post {
    [x: string]: any;
    title: string;
    content: string;
    imageUrl?: string; // 'imageUrl' is optional if not always present in your data
    item: string;
    // Other properties...
  }


const Post = () => {

    const {  } = useCategoriesContext();




  const { id } = useParams(); // Accessing the postId from the URL params
  const [post, setPost] = useState<Post | null>(null);

  useEffect(() => {
    const fetchPost = async () => {
      try {
        // Fetching the single post based on postId
        const response = await axios.get(`http://localhost:3001/api/posts/${id}`);
        setPost(response.data); // Setting the retrieved post data to state
      } catch (error) {
        console.error('Error fetching post:', error);
      }
    };

    fetchPost(); // Fetch the post when the component mounts
  }, [id]); // Re-fetch post whenever postId changes

  return (
    <div>
      {post ? (
        <div>
            {post.map((item : any) => (
                 <div key={item.id}><h2>{item.title}</h2><p>{post.content}</p>
                 {item.imageUrl && <img src={item.imageUrl} alt="Post" />}
                 </div>
            ))}

        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Post;
