import React, { createContext, useContext, useEffect, useState, ReactNode } from 'react';
import axios from 'axios';

interface CategoriesProviderProps {
  children: ReactNode;
}

interface Category {
    category_id: string;
    content:string;
    title:string;
    post_img:string;

}

type UseContext = CategoriesProviderProps & Category

export const CategoriesProvider: React.FC<UseContext> = ({ children }) => {
  const [posts, setPosts] = useState<Category[]>([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get<Category[]>('http://localhost:3000/api/posts/');
        setPosts(response.data);
        console.log(response.data, 'response data hook context');
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <CategoriesContext.Provider value={posts}>
      {children}
    </CategoriesContext.Provider>
  );
};
