import React, { useState, useEffect } from 'react';
import { useCategoriesContext } from "../contexts/usePostProvider/usePostList";

import axios from 'axios';
import { useParams } from 'react-router-dom';


interface Post {
    [x: string]: any;
    title: string;
    content: string;
    imageUrl?: string; // 'imageUrl' is optional if not always present in your data
    item: string;
    // Other properties...
  }


  const Post = () => {
    const { } = useCategoriesContext();
    const { id } = useParams();
    const [post, setPost] = useState<Post[]>([]); // Initialize as an empty array for multiple posts

    useEffect(() => {
      const fetchPost = async () => {
        try {
          const response = await axios.get(`http://localhost:3001/api/posts/${id}`);
          setPost([response.data]); // Wrap response.data in an array or handle if it's already an array
        } catch (error) {
          console.error('Error fetching post:', error);
        }
      };

      fetchPost();
    }, [id]);

    return (
      <React.Fragment>
        {post.length > 0 ? (
          post.map((item: Post) => (
            <div key={item.id}>
              <h2 style={{ color: 'red' }}>{item.title}</h2>
              <p>{item.content}</p>
              {item.imageUrl && <img src={item.imageUrl} alt="Post" />}
            </div>
          ))
        ) : (
          <p>Loading...</p>
        )}
      </React.Fragment>
    );
  };

  export default Post;
