import React, { useRef, useState, ChangeEvent } from 'react';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';
import styles from './YourStyles.module.css'; // Import your CSS styles

interface FormData {
  title: string;
  content: string;
}

const PostPage: React.FC = () => {
  const titleRef = useRef<HTMLInputElement>(null);
  const contentRef = useRef<HTMLTextAreaElement>(null);
  const selectRef = useRef<HTMLSelectElement>(null);

  const [selectedFile, setSelectedFile] = useState<File | null>(null);

  const handleFileChange = (event: ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedFile(file);
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      console.error('No file selected.');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response: any = await axios.post('http://localhost:3001/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      const { uploadedImg } = response.data; // Assuming backend returns the uploaded file path

      const title = titleRef.current?.value || '';
      const content = contentRef.current?.value || '';
      const selectedCategory = selectRef.current?.value || '';

      console.log(title, content, selectedCategory, uploadedImg);

      await axios.post<FormData>('http://localhost:3001/api/posts/', {
        title,
        content,
        selectedCategory,
        uploadedImg,
      });
    } catch (error) {
      console.error('Error', error);
    }
  };

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    // Other logic if needed
  };

  return (
    <div className={styles.postpage_container}>
      <h1 style={{ color: 'black' }}>PostPage</h1>
      <Form onSubmit={handleSubmit} style={{ padding: '5rem', height: '100%' }}>
        {/* Your form fields */}
        <Form.Control
          aria-label="Default select example"
          as="select"
          ref={selectRef}
          className={styles.select_category_wrapper}
        >
          {/* Your select options */}
        </Form.Control>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
      <div className={styles.file_upload}>
        <Form.Label>Add image</Form.Label>
        <Form.Control type="file" onChange={handleFileChange} accept="image/*" />
        <button onClick={handleUpload}>Upload</button>
      </div>
    </div>
  );
};

export default PostPage;
